import React, { Component } from "react";
import Question from "./Question";

class Game extends Component {
  state = {
    questions: [
      { prompt: "q2", answer: "a2" },
      { prompt: "q1", answer: "a1" },
      { prompt: "q3", answer: "a3" },
      { prompt: "q4", answer: "a4" },
      { prompt: "q5", answer: "a5" },
      { prompt: "q6", answer: "a6" },
    ],
  };
  render() {
    return (
      <div>
        {this.state.questions.map((q) => (
          <Question q={q} />
        ))}
      </div>
    );
  }
}

export default Game;
