import React, { Component } from "react";
import Footer from "./Footer";
import NavBar from "./NavBar";
import Question from "./Question";

class Game extends Component {
  state = {
    questions: [
      {
        num: 1,
        olelo: "'Ehaki",
        prompt: "What month is Hawaiian History Month?",
        answer: "September",
      },
      {
        num: 2,
        olelo: "'Elua",
        prompt:
          "What sport was invented by the kﾄ］aka maoli (the native hawaiian people) and popularized in the US by Duke Kahanamoku?",
        answer: "Surfing",
      },
      { num: 3, olelo: "'Ekolu", prompt: "q3", answer: "a3" },
      { num: 4, olelo: "'Ehﾄ―

q4", answer: "a4" },
      { num: 5, olelo: "'Elima", prompt: "q5", answer: "a5" },
      {
        num: 6,
        olelo: "'Eono",
        prompt:
          "For how many years was the Kingdom of Hawai'i a formally recognized sovereign nation prior to the overthrow?",
        answer: "80",
      },
    ],
    score: 0,
    answered: 0,
  };

  handleScore = (correct) => {
    const update = { answered: this.state.answered + 1 };
    this.setState(
      correct ? { ...update, score: this.state.score + 1 } : update
    );
  };

  render() {
    return (
      <div id="game-root">
        <NavBar score={this.state.score} />
        <div className="container">
          {this.state.questions.map((q) => (
            <Question key={q.num} q={q} onScore={this.handleScore} />
          ))}
        </div>
        <div className="text-center">
          <a href="#game-root">
            <button className="btn btn-primary">Back To Top</button>
          </a>
        </div>
        <Footer />
      </div>
    );
  }
}

export default Game;
